// Compile cost optimization recommendations
const resourceUsage = $('analyze-resource-usage').all();
const queryPerformance = $('analyze-query-performance').all();
const modelCosts = $('analyze-model-costs').all();
const dataRetention = $('analyze-data-retention').all();

let optimizations = [];
let potentialSavings = 0;
let criticalActions = [];
let recommendations = [];

// Analyze resource usage
let totalStorageUsed = 0;
let largeUnusedTables = [];

for (const resource of resourceUsage) {
  totalStorageUsed += resource.json.size_mb;

  // Identify large tables that haven't been updated recently
  if (resource.json.size_mb > 100 && resource.json.days_since_update > 30) {
    largeUnusedTables.push({
      table: resource.json.table_name,
      size_mb: resource.json.size_mb,
      days_unused: resource.json.days_since_update
    });

    optimizations.push({
      type: 'storage_optimization',
      action: 'archive_or_compress',
      target: resource.json.table_name,
      potential_savings_mb: resource.json.size_mb * 0.7, // Assume 70% compression
      priority: 'medium'
    });

    potentialSavings += resource.json.size_mb * 0.7 * 0.1; // Assume $0.10 per MB per month
  }

  // Identify tables with inefficient storage
  if (resource.json.bytes_per_row > 1000) {
    optimizations.push({
      type: 'schema_optimization',
      action: 'optimize_schema',
      target: resource.json.table_name,
      current_bytes_per_row: resource.json.bytes_per_row,
      priority: 'low'
    });
  }
}

// Analyze query performance and frequency
let highFrequencyJobs = [];
let underperformingJobs = [];

for (const query of queryPerformance) {
  if (query.json.frequency_category === 'high_frequency') {
    highFrequencyJobs.push({
      job: query.json.job_name,
      frequency: `${query.json.interval_value} ${query.json.interval_field}`,
      execution_time: query.json.avg_execution_time_seconds
    });

    // Suggest optimization for high-frequency jobs
    if (query.json.avg_execution_time_seconds > 30) {
      optimizations.push({
        type: 'query_optimization',
        action: 'optimize_high_frequency_job',
        target: query.json.job_name,
        current_execution_time: query.json.avg_execution_time_seconds,
        potential_savings_seconds: query.json.avg_execution_time_seconds * 0.5,
        priority: 'high'
      });

      criticalActions.push(`Optimize high-frequency job: ${query.json.job_name}`);
    }
  }

  // Identify jobs that might be running too frequently
  if (query.json.frequency_category === 'high_frequency' && query.json.interval_value <= 2) {
    optimizations.push({
      type: 'frequency_optimization',
      action: 'reduce_frequency',
      target: query.json.job_name,
      current_frequency: `${query.json.interval_value} ${query.json.interval_field}`,
      suggested_frequency: `${query.json.interval_value * 2} ${query.json.interval_field}`,
      priority: 'medium'
    });
  }
}

// Analyze model costs and performance
let underperformingModels = [];
let staleModels = [];

for (const model of modelCosts) {
  if (model.json.model_status === 'underperforming') {
    underperformingModels.push({
      model: model.json.model_name,
      accuracy: model.json.accuracy_score,
      action: model.json.recommended_action
    });

    optimizations.push({
      type: 'model_optimization',
      action: 'retrain_or_replace_model',
      target: model.json.model_name,
      current_accuracy: model.json.accuracy_score,
      priority: 'high'
    });

    criticalActions.push(`Address underperforming model: ${model.json.model_name}`);
  }

  if (model.json.model_status === 'stale') {
    staleModels.push({
      model: model.json.model_name,
      days_stale: model.json.days_since_evaluation
    });

    optimizations.push({
      type: 'model_maintenance',
      action: 'evaluate_model_performance',
      target: model.json.model_name,
      days_since_evaluation: model.json.days_since_evaluation,
      priority: 'medium'
    });
  }
}

// Analyze data retention opportunities
let dataArchivalOpportunities = [];

for (const retention of dataRetention) {
  if (retention.json.pct_older_1d > 50 || retention.json.pct_older_30d > 30) {
    dataArchivalOpportunities.push({
      table: retention.json.table_name,
      total_records: retention.json.total_records,
      archival_candidates: retention.json.records_older_30d || retention.json.records_older_1d,
      suggestion: retention.json.optimization_suggestion
    });

    const archivalSavings = (retention.json.records_older_30d || retention.json.records_older_1d) * 0.001; // Assume $0.001 per record per month

    optimizations.push({
      type: 'data_archival',
      action: 'archive_old_data',
      target: retention.json.table_name,
      records_to_archive: retention.json.records_older_30d || retention.json.records_older_1d,
      potential_savings_monthly: archivalSavings,
      priority: 'medium'
    });

    potentialSavings += archivalSavings;
  }
}

// Generate specific recommendations
recommendations = [
  ...criticalActions,
  `Archive ${dataArchivalOpportunities.length} tables with old data`,
  `Optimize ${highFrequencyJobs.length} high-frequency jobs`,
  `Review ${underperformingModels.length} underperforming models`,
  `Compress ${largeUnusedTables.length} large unused tables`
].filter(rec => rec && !rec.includes('0 '));

// Calculate priority scores
const highPriorityOptimizations = optimizations.filter(opt => opt.priority === 'high').length;
const mediumPriorityOptimizations = optimizations.filter(opt => opt.priority === 'medium').length;
const lowPriorityOptimizations = optimizations.filter(opt => opt.priority === 'low').length;

// Compile final optimization report
const optimizationReport = {
  timestamp: new Date().toISOString(),
  summary: {
    total_optimizations_identified: optimizations.length,
    high_priority: highPriorityOptimizations,
    medium_priority: mediumPriorityOptimizations,
    low_priority: lowPriorityOptimizations,
    estimated_monthly_savings: Math.round(potentialSavings * 100) / 100,
    critical_actions_required: criticalActions.length
  },
  resource_analysis: {
    total_storage_mb: Math.round(totalStorageUsed),
    large_unused_tables: largeUnusedTables.length,
    storage_optimization_opportunities: largeUnusedTables
  },
  query_analysis: {
    high_frequency_jobs: highFrequencyJobs.length,
    underperforming_jobs: underperformingJobs.length,
    frequency_optimization_opportunities: highFrequencyJobs
  },
  model_analysis: {
    underperforming_models: underperformingModels.length,
    stale_models: staleModels.length,
    model_optimization_opportunities: [...underperformingModels, ...staleModels]
  },
  data_retention_analysis: {
    archival_opportunities: dataArchivalOpportunities.length,
    tables_with_old_data: dataArchivalOpportunities
  },
  optimizations: optimizations,
  recommendations: recommendations,
  next_review_date: new Date(Date.now() + 6 * 60 * 60 * 1000).toISOString() // 6 hours from now
};

return [{ json: optimizationReport }];

