-- 1. Archive old real-time price data
CREATE TABLE IF NOT EXISTS crypto_data_db.archived_real_time_prices LIKE crypto_data_db.real_time_prices;

INSERT INTO crypto_data_db.archived_real_time_prices
SELECT * FROM crypto_data_db.real_time_prices
WHERE last_updated < DATE_SUB(NOW(), INTERVAL 24 HOUR);

DELETE FROM crypto_data_db.real_time_prices
WHERE last_updated < DATE_SUB(NOW(), INTERVAL 24 HOUR);

-- 2. Archive old social sentiment data
CREATE TABLE IF NOT EXISTS crypto_data_db.archived_social_sentiment LIKE crypto_data_db.social_sentiment;

INSERT INTO crypto_data_db.archived_social_sentiment
SELECT * FROM crypto_data_db.social_sentiment
WHERE last_updated < DATE_SUB(NOW(), INTERVAL 30 DAY);

DELETE FROM crypto_data_db.social_sentiment
WHERE last_updated < DATE_SUB(NOW(), INTERVAL 30 DAY);

-- 3. Archive old news data
CREATE TABLE IF NOT EXISTS crypto_data_db.archived_crypto_news LIKE crypto_data_db.crypto_news_real_time;

INSERT INTO crypto_data_db.archived_crypto_news
SELECT * FROM crypto_data_db.crypto_news_real_time
WHERE published_at < DATE_SUB(NOW(), INTERVAL 90 DAY);

DELETE FROM crypto_data_db.crypto_news_real_time
WHERE published_at < DATE_SUB(NOW(), INTERVAL 90 DAY);

-- Optimize Database Performance

-- 1. Update table statistics
ANALYZE TABLE crypto_data_db.real_time_prices;
ANALYZE TABLE crypto_data_db.defi_real_time;
ANALYZE TABLE crypto_data_db.social_sentiment;
ANALYZE TABLE crypto_data_db.daily_technical_indicators;
ANALYZE TABLE user_data_db.learning_sessions;
ANALYZE TABLE user_data_db.user_trades;
ANALYZE TABLE user_data_db.social_interactions;

-- 2. Optimize tables
OPTIMIZE TABLE crypto_data_db.real_time_prices;
OPTIMIZE TABLE crypto_data_db.defi_real_time;
OPTIMIZE TABLE crypto_data_db.social_sentiment;

-- 3. Check for unused indexes
SELECT 
  table_schema,
  table_name,
  index_name,
  'Consider dropping if unused' as recommendation
FROM information_schema.statistics
WHERE table_schema IN ('crypto_data_db', 'user_data_db')
AND index_name != 'PRIMARY'
GROUP BY table_schema, table_name, index_name;

