parameters.jsCode:
// Compile monitoring dashboard data
const systemHealth = $('system-health-check').all();
const knowledgeBaseMetrics = $('knowledge-base-metrics').all();
const dataQuality = $('data-quality-monitoring').all();
const modelPerformance = $('model-performance-monitoring').all();
const jobMonitoring = $('job-execution-monitoring').all();
const userActivity = $('user-activity-monitoring').all();

// Calculate overall system health score
let healthScore = 100;
let alerts = [];
let warnings = [];

// Check data quality
for (const dq of dataQuality) {
  if (dq.json.avg_quality_score < 0.7) {
    healthScore -= 15;
    alerts.push(`Low data quality in ${dq.json.table_name}: ${(dq.json.avg_quality_score * 100).toFixed(1)}%`);
  } else if (dq.json.avg_quality_score < 0.8) {
    healthScore -= 5;
    warnings.push(`Data quality warning in ${dq.json.table_name}: ${(dq.json.avg_quality_score * 100).toFixed(1)}%`);
  }

  if (dq.json.minutes_since_update > 30) {
    healthScore -= 10;
    alerts.push(`Stale data in ${dq.json.table_name}: ${dq.json.minutes_since_update} minutes old`);
  }
}

// Check model performance
for (const mp of modelPerformance) {
  if (mp.json.status === 'critical') {
    healthScore -= 20;
    alerts.push(`Critical model performance: ${mp.json.model_name} accuracy ${(mp.json.accuracy_score * 100).toFixed(1)}%`);
  } else if (mp.json.status === 'warning') {
    healthScore -= 10;
    warnings.push(`Model performance warning: ${mp.json.model_name} accuracy ${(mp.json.accuracy_score * 100).toFixed(1)}%`);
  }
}

// Check job execution
for (const job of jobMonitoring) {
  if (job.json.job_status === 'overdue') {
    healthScore -= 15;
    alerts.push(`Overdue job: ${job.json.job_name}`);
  } else if (job.json.job_status === 'disabled') {
    healthScore -= 5;
    warnings.push(`Disabled job: ${job.json.job_name}`);
  }
}

// Determine overall status
let overallStatus;
if (healthScore >= 90) {
  overallStatus = 'excellent';
} else if (healthScore >= 80) {
  overallStatus = 'good';
} else if (healthScore >= 70) {
  overallStatus = 'warning';
} else {
  overallStatus = 'critical';
}

// Compile dashboard data
const dashboardData = {
  timestamp: new Date().toISOString(),
  overall_health: {
    score: Math.max(0, healthScore),
    status: overallStatus,
    alerts: alerts,
    warnings: warnings
  },
  system_metrics: {
    active_jobs: systemHealth[0]?.json?.active_jobs || 0,
    active_tables: systemHealth[0]?.json?.active_tables || 0
  },
  knowledge_bases: {
    crypto_market_intel: knowledgeBaseMetrics.find(kb => kb.json.metric_type === 'knowledge_base_metrics'),
    user_behavior: knowledgeBaseMetrics.find(kb => kb.json.metric_type === 'user_behavior_metrics'),
    educational_content: knowledgeBaseMetrics.find(kb => kb.json.metric_type === 'educational_metrics')
  },
  data_quality: dataQuality.map(dq => ({
    table: dq.json.table_name,
    quality_score: dq.json.avg_quality_score,
    record_count: dq.json.record_count,
    freshness_minutes: dq.json.minutes_since_update
  })),
  model_performance: modelPerformance.map(mp => ({
    model: mp.json.model_name,
    accuracy: mp.json.accuracy_score,
    drift: mp.json.prediction_drift,
    status: mp.json.status
  })),
  job_status: jobMonitoring.map(job => ({
    name: job.json.job_name,
    status: job.json.job_status,
    last_execution_minutes: job.json.minutes_since_execution
  })),
  user_activity: {
    active_users_1h: userActivity[0]?.json?.active_users_1h || 0,
    total_interactions_1h: userActivity[0]?.json?.total_interactions_1h || 0,
    avg_sentiment_1h: userActivity[0]?.json?.avg_sentiment_1h || 0,
    questions_1h: userActivity[0]?.json?.questions_1h || 0,
    trades_1h: userActivity[0]?.json?.trades_1h || 0,
    learning_sessions_1h: userActivity[0]?.json?.learning_sessions_1h || 0
  },
  recommendations: []
};

// Generate recommendations
if (alerts.length > 0) {
  dashboardData.recommendations.push('Address critical alerts immediately');
}
if (warnings.length > 0) {
  dashboardData.recommendations.push('Review and resolve warnings');
}
if (dashboardData.user_activity.active_users_1h < 10) {
  dashboardData.recommendations.push('Low user activity - check user engagement strategies');
}
if (dashboardData.data_quality.some(dq => dq.quality_score < 0.8)) {
  dashboardData.recommendations.push('Improve data quality monitoring and validation');
}
if (dashboardData.model_performance.some(mp => mp.accuracy < 0.8)) {
  dashboardData.recommendations.push('Retrain underperforming models');
}

if (dashboardData.recommendations.length === 0) {
  dashboardData.recommendations.push('System operating optimally - continue monitoring');
}

return [{ json: dashboardData }];

